class Solution:
    def longestPalindrome(self, s: str) -> int:
        temp = {}
        for i,x in enumerate(s):
            temp[x] = temp.get(x,0) + 1
        ans = 0
        has_odd = False
        for i in temp.values():
            if i % 2 == 0:
                ans += i
            else:
                ans += i - 1
                has_odd = True
        return ans + 1 if has_odd else ans
s = Solution()
a = ""
print(s.longestPalindrome(a))